sys_atf_step_config.description_generator:
function generateDescription()
{
	//var strTable = step.inputs.u_table;
    //var strDescription = 'DataSilos Check ACLs for table: ['+strTable+']\n';
    var strDescription = 'DataSilos Check ACLs';

	return strDescription;
}
generateDescription();

sys_atf_step_config.step_execution_generator:
(function executeStep(inputs, outputs, stepResult, timeout)
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetBoolValue = x_snc_devtools.GetBoolValue;

	stepResult.setSuccess();

	var strTable = inputs.u_table;
	var strMessage = 'DataSilos Check ACLs for table: ['+strTable+']\n';

   function StringRemoveCharacters(strInput,strBlacklist)     
   {
	   strInput = GetStringValue(strInput);
	   strBlacklist = GetStringValue(strBlacklist);
      var strOutput = "";
      for (var nIndex = 0; nIndex < strInput.length; nIndex++)
      {
         if (strBlacklist.indexOf(strInput[nIndex]) == -1)
         {
            strOutput += strInput[nIndex];
         }
      }
      return strOutput;
   }

	function CheckACLsByName(strName)
	{
		var grACL = new GlideRecord('sys_security_acl');

		grACL.addQuery('active','=','true');
		grACL.addQuery('name','=',strName);
		grACL.addQuery('operation','=','read');
		grACL.query();

		if (grACL.getRowCount() == 0)
		{
			strMessage += 'No active ACLs have been found with name "'+strName+'"!\n';
			stepResult.setFailed();
		}
		while (grACL.next())
		{
			var strDescription = GetStringValue(grACL.description);
			strMessage += 'Checking ACL '+grACL.sys_id+' / "'+strName+'" / "'+strDescription+'"\n';

			var bAdvanced = GetBoolValue(grACL.advanced);
			if (bAdvanced != true)
			{
				strMessage += 'Failed: ACL is not set to advanced!\n';
				stepResult.setFailed();
			}
			var strScript = GetStringValue(grACL.script);
			strScript = StringRemoveCharacters(strScript,' \n\r\t');
			if (strScript == '')
			{
				strMessage += 'Failed: Script is empty!\n';
				stepResult.setFailed();
			}

			var bScriptOK = false;
			if (strScript == 'answer=x_snc_datasilos.ExecuteACL(current);')
			{
				bScriptOK = true;
			}
			if (strScript == 'x_snc_datasilos.ExecuteACL(current);')
			{
				bScriptOK = true;
			}
			var strScriptBegin = 'var bAnswer = false';
			strScriptBegin = StringRemoveCharacters(strScriptBegin,' \n\r\t');

			var strScriptEnd = 'if (bAnswer == true) { bAnswer = x_snc_datasilos.ExecuteACL(current); } answer = bAnswer;';
			strScriptEnd = StringRemoveCharacters(strScriptEnd,' \n\r\t');
			if (strScript.indexOf(strScriptBegin) == 0)
			{
				strMessage += 'Found pattern: "'+strScriptBegin+'"\n';
				var strSubstr = strScript.substr(-strScriptEnd.length);
				strMessage += 'Substr: "'+strSubstr+'"\n';

				if (strSubstr == strScriptEnd)
				{
					bScriptOK = true;
				}

			}
			if (bScriptOK == false)
			{
				strMessage += 'Failed: Script does not contain a valid implementation with "x_snc_datasilos.ExecuteACL(current)"!\n';
				stepResult.setFailed();
			}
		}

	}

	CheckACLsByName(strTable);
	CheckACLsByName(strTable+'.*');

	stepResult.setOutputMessage(strMessage);



}(inputs, outputs, stepResult, timeout));
